#!/bin/bash

set -eux

name="$1"
private_key="$2"
address="$3"
post_up="$4"
post_down="$5"
after="$6"

umask 077
cat > "/etc/wireguard/$name.conf" << EOF
# Generated by Qrystal-mio for network $name. DO NOT EDIT.
# This file will be overwritten during sync.
[Interface]
PrivateKey=$private_key
Address=$address

PostUp=$post_up
PostDown=$post_down

# After:
$after
EOF

ifaces=($(wg show interfaces))

echo interfaces ${ifaces[*]}

log=$(mktemp)

if [[ " ${ifaces[*]} " =~ " $name " ]]; then
	echo down
	# TODO: syncconf if ips don't change
	wg-quick down "$name" 2> $log || 1>&2 cat $log
fi

wg-quick up "$name" 2> $log || 1>&2 cat $log
rm $log
